                        }
                        else
                        {
                            $data[$index] = $m[1];
                            $index = null;
                        }
                        $broken = preg_replace($re, '', $broken);
                    }
                    break;

                case 'i:': // key or value
                    $re = '/^i:(\d+);/';
                    if (preg_match($re, $broken, $m))
                    {
                        if ($index === null)
                        {
                            $index = (int) $m[1];
                        }
                        else
                        {
                            $data[$index] = (int) $m[1];
                            $index = null;
                        }
                        $broken = preg_replace($re, '', $broken);
                    }
                    break;

                case 'b:': // value only
                    $re = '/^b:([01]);/';
                    if (preg_match($re, $broken, $m))
                    {
                        $data[$index] = (bool) $m[1];
                        $index = null;
                        $broken = preg_replace($re, '', $broken);
                    }
                    break;

                case 'a:': // value only
                    $re = '/^a:\d+:\{/';
                    if (preg_match($re, $broken, $m))
                    {
                        $broken			= preg_replace('/^a:\d+:\{/', '', $broken);
                        $data[$index]	= repairSerializedArray_R($broken);
                        $index = null;
                    }
                    break;

                case 'O:':
                    $re = '/^O:\d+:"([^\"]*)":\d+:\{/';
                    if (preg_match($re, $broken, $m))
                    {
                        $className = $m[1];
                        $broken = preg_replace('/^O:\d+:"([^\"]*)":\d+:\{/', '', $broken);
                        $objectData = repairSerializedArray_R($broken);
                        $data[$index] = (object)$objectData;
                        $index = null;
                    }
                    break;

                case 'N;': // value only
                    $broken = substr($broken, 2);
                    $data[$index]	= null;
                    $index = null;
                    break;
            }
        }
    }

    return $data;
}

function fix_serialized($string) {
    // securities
    if ( !preg_match('/^[aOs]:/', $string) ) return $string;
    if ( @unserialize($string) !== false ) return $string;
    $string = preg_replace("%\n%", "", $string);
    // doublequote exploding
    $data = preg_replace('%";%', "µµµ", $string);
    $tab = explode("µµµ", $data);
    $new_data = '';
    foreach ($tab as $line) {
        $new_data .= preg_replace_callback('%\bs:(\d+):"(.*)%', function ($matches){
            $string = $matches[2];
            $right_length = strlen($string); // yes, strlen even for UTF-8 characters, PHP wants the mem size, not the char count
            return 's:' . $right_length . ':"' . $string . '";';
        }, $line);
    }
    return $new_data;
}

